package com.company.java.printASCII;

public class PrintAsciiValueExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// characters whose ASCII value to be found
		char ch1 = 'a';
		char ch2 = 'b';

		// casting or converting a character into int type
		int ascii1 = (int) ch1;
		int ascii2 = (int) ch2;

		System.out.println("The ASCII value of " + ch1 + " is " + ascii1);
		System.out.println("The ASCII value of " + ch2 + " is " + ascii2);

	}

}
